mapper.update:

		update board
		set
             bo_title = #{boTitle}
             , bo_content = #{boContent}
             , bo_writer = #{boWriter}
        where bo_no = #{boNo}

mapper.select:

select b.*
from (

    select a.* , row_number() over (order by a.bo_no desc) rnum
    from (
        select 
            BO_NO, BO_TITLE, BO_CONTENT, BO_WRITER, BO_DATE, BO_HIT
        from board
        order by bo_no desc
    ) a
) b 


	where b.rnum >= #{startRow} and b.rnum <= #{endRow}
