mapper.insert:
insert into tagboard (
	bo_no, bo_title, bo_content, bo_writer, bo_date, bo_hit
) values (
	#{boNo}, #{boTitle}, #{boContent}, #{boWriter}, sysdate, 0
)

insert into tag (
	bo_no
	, tag_name
) values (
	#{boNo}, #{tagName}
)

mapper.select:
select a.* from (select BO_NO , BO_TITLE , BO_CONTENT , BO_WRITER , BO_DATE , BO_HIT , row_number()over(order by bo_no desc) as rno from tagboard where 1=1
and bo_title like '%'||#{searchWord}||'%'
and bo_writer like '%'||#{searchWord}||'%'
) a
where a.rno >= #{startRow} and a.rno <= #{endRow}

mapper.update:
update tagboard
set bo_title = #{boTitle}
	, bo_content = #{boContent}
	, bo_date = sysdate
where bo_no = #{boNo}

update tagboard
set bo_hit = bo_hit + 1
where bo_no = ${boNo}
